import React from 'react'
import * as rb from 'react-bootstrap'
import {ReactComponent as Logo} from '../Imgs/upload.svg'

class TopNavBar extends React.Component{

    constructor(){
        super();

        this.state = {
            dropDownValue: "None"
        }
    }

    dropdownClicked(text){
        this.setState({dropDownValue: text})
    }

    render(){
        
        let suspected = ["None", "Jhonny", "Danny", "Gabby", "lonny"].map(sus => <rb.NavDropdown.Item eventKey={sus}>{sus}</rb.NavDropdown.Item>)
        
        const inputFile = useRef(null)

        const onButtonClick = () => {
            // `current` points to the mounted file input element
           inputFile.current.click();
          };
        
        return (
            
            <rb.Navbar bg="white">
                <rb.Navbar.Brand style={{fontSize:30}}>
                    Filter:
                </rb.Navbar.Brand>
                <rb.Navbar.Toggle />
                <rb.Nav onSelect={(eventKey) => this.dropdownClicked(eventKey)}>
                    <rb.NavDropdown title={this.state.dropDownValue}>
                        {suspected}
                    </rb.NavDropdown>
                </rb.Nav>
                <rb.Navbar.Collapse className="justify-content-end">
                    <rb.Button className="text-right" id="importCases" style={{textSize:15}} onClick={onButtonClick}>
                        <Logo style={{width:30, height:30, paddingRight:7.4}} id="uploadLogo"/>
                        IMPORT SUSPECTED CASES
                    </rb.Button>
                    <input type='file' id='file' ref={inputFile} style={{display: 'none'}}/>
                </rb.Navbar.Collapse>
            </rb.Navbar>
        )

    }

}

export default TopNavBar
