import React, { useRef } from 'react'
import * as rb from 'react-bootstrap'
import {ReactComponent as Logo} from '../Imgs/upload.svg'
import Papa from 'papaparse'
import axios from 'axios'
// import Modal from './Modal'


class TopNavBar extends React.Component{

    constructor(){
        super();

        this.uploadFile = this.uploadFile.bind(this);
        this.handleClick = this.handleClick.bind(this);
        this.state = {
            dropDownValue: "None",
            dataList: ["None"],
            suspectCases: [],
            error: false,
            errorMsg: "",
            showModal: false
        }
    }

    handleClick(e) {
        this.refs.fileUploader.click();
    }

    // getData(){

    //     const url = "http://localhost:8080/listAllSuspectedCases";
    //     fetch(url).then(response => response.json()).then(data => console.log(data))

    //     // const response = axios.get("http://localhost:8080/list", { headers: {'Access-Control-Allow-Origin': 'http://localhost:3000', 'Access-Control-Allow-Credentials': 'true'}})
    //     // console.log(response.data)
    // }

    postData(file1, file2){

        fetch("http://localhost:8080/addSuspect", {
            "method": "POST",
            "headers": {
                "content-type": "application/json"
            },
            "body": file1
        })
        .then(response => {
            console.log(response)
        })
        .then(() => {
            this.postMovements(file2)
        })
        .catch(err => {
            console.log(err);
        });
        
    }

    postMovements(file2){
        fetch("http://localhost:8080/addMovement", {
            "method": "POST",
            "headers": {
                "content-type": "application/json"
            },
            "body": file2
        })
        .then(response => {
            console.log(response);
        })
        .catch(err => {
            console.log(err);
        });
    }

    uploadFile(event) {
        
        let file
        let file2

        //Check if the movements andsuspected case profiles are uploaded
        if(event.target.files.length !== 2){
            this.setState({error:true, errorMsg:"You need to upload at least 2 files!"})
            return
        }

        //Check if the file is the correct file
        console.log("Files:")
        for (var i=0, l=event.target.files.length; i<l; i++) {
            console.log(event.target.files[i].name);

            if (event.target.files[i].name.includes("_suspected")){
                file = event.target.files[i]
            }
            else if (event.target.files[i].name.includes("_movements")){
                file2 = event.target.files[i]
            }
            else{
                this.setState({error:true, errorMsg:"You have uploaded invalid files! Please rename the files to <filename>_suspected (For suspected cases) or <filename>_movement (For suspected case movement)"})
                return
            }
        }

        var fileC1, fileC2

        //Reads the first file (Suspected profile)
        if (file) {
            const reader = new FileReader();
            reader.onload = () => {
                // Use reader.result
                const lols = Papa.parse(reader.result, {header: true, skipEmptyLines: true}, )
                
                console.log(lols.data)

                // Posting csv data into db
                // this.postData('"' + JSON.stringify(lols.data) + '"')
                fileC1 = JSON.stringify(lols.data)

                // Adds names into dropdown
                this.setState({dataList: ["None", ...lols.data.map(names => names.firstName + " " + names.lastName)]})

                const data = lols.data
                this.setState({suspectCases: data})
            }
            reader.readAsText(file)
        }

        if (file2) {
            const reader = new FileReader();
            reader.onload = () => {
                // Use reader.result
                const lols = Papa.parse(reader.result, {header: true, skipEmptyLines: true}, )
                
                console.log(lols.data)

                // Posting csv data into db
                fileC2 = JSON.stringify(lols.data)

                // // Adds names into dropdown
                // this.setState({dataList: ["None", ...lols.data.map(names => names.firstName + " " + names.lastName)]})

                // const data = lols.data
                // this.setState({suspectCases: data})
            }
            reader.readAsText(file2)
        }

        // this.postData(fileC1, fileC2)
        
    }

    dropdownClicked(text) {
        this.setState({dropDownValue: text})
    }

    close(){
        
    }

    render() {
        
        let suspected = this.state.dataList.map(sus => <rb.NavDropdown.Item eventKey={sus}>{sus}</rb.NavDropdown.Item>)
        
        let displaySetting

        if(this.state.dataList.length === 1){
            displaySetting = "block"
        }
        else{
            displaySetting = "none"
        }
        
        return (
            <div>
                <rb.Navbar bg="white">
                    <rb.Navbar.Brand style={{fontSize:30}}>
                        Filter:
                    </rb.Navbar.Brand>
                    <rb.Navbar.Toggle />
                    <rb.Nav onSelect={(eventKey) => this.dropdownClicked(eventKey)}>
                        <rb.NavDropdown title={this.state.dropDownValue}>
                            {suspected}
                        </rb.NavDropdown>
                    </rb.Nav>
                    <rb.Navbar.Collapse className="justify-content-end">

                        <rb.Button className="text-right" id="importCases" style={{textSize:15}} onClick={() => this.setState({showModal: true})}>
                            <Logo style={{width:30, height:30, paddingRight:7.4}} id="uploadLogo"/>
                            IMPORT SUSPECTED CASES
                        </rb.Button>
                        {/* <input type="file" id="file" ref="fileUploader" style={{display: "none"}} onChange={this.uploadFile} multiple/> */}

                        {/* <rb.Button className="text-right" id="importCases" style={{textSize:15}} onClick={this.handleClick.bind(this)}>
                            <Logo style={{width:30, height:30, paddingRight:7.4}} id="uploadLogo"/>
                            IMPORT SUSPECTED CASES
                        </rb.Button>
                        <input type="file" id="file" ref="fileUploader" style={{display: "none"}} onChange={this.uploadFile} multiple/> */}
                    </rb.Navbar.Collapse>
                </rb.Navbar>

                <div>
                    <rb.Modal show={this.state.showModal} onHide={() => this.setState({showModal: false}) } animation={false}>
                        <rb.Modal.Header closeButton>
                            <rb.Modal.Title>Upload Suspect Cases Files</rb.Modal.Title>
                        </rb.Modal.Header>

                        <rb.Modal.Body>

                            <div className="float-lect">
                                Profile of suspected case
                                <br/>
                                <rb.Button variant="secondary" >
                                    Upload
                                </rb.Button>
                            </div>

                            <div className="float-right">
                                Movement of suspected case
                                <br/>
                                <rb.Button variant="secondary" >
                                    Upload
                                </rb.Button>
                            </div>
                            
                        </rb.Modal.Body>

                        <rb.Modal.Footer> 
                            

                            <rb.Button variant="primary">
                                Upload 
                            </rb.Button>
                        </rb.Modal.Footer>
                    </rb.Modal>
                </div>

                <div>
                    <rb.Toast onClose={() => this.setState({error: false})} show={this.state.error}>
                        <rb.Toast.Header>
                            <img
                                src="holder.js/20x20?text=%20"
                                className="rounded mr-2"
                                alt=""
                            />
                            <strong className="mr-auto">Error!</strong>
                            {/* <small>11 mins ago</small> */}
                        </rb.Toast.Header>
                        <rb.Toast.Body>{this.state.errorMsg}</rb.Toast.Body>
                    </rb.Toast>
                </div>
                
                <div>
                    {/* {this.state.suspectCases} */}
                </div>

                <div style={{verticalAlign:"middle", display:displaySetting}}>
                    <h1 style={{color:"#424761", textAlign:"center"}}>Import some data to begin</h1>
                </div>
            </div>
        )
    }

}

export default TopNavBar
