import React from 'react';
import './App.css';
import NavBar from './Components/Navbar'
import Graph from './Components/Graphs';

import 'bootstrap/dist/css/bootstrap.min.css';
import './style.css'

class App extends React.Component {

  constructor(){
    super();
    this.state = {
      NavbarState: {}
    }
  }

  handleNavbar = (e) => {this.setState({NavbarState: e})}

  render(){

    let displaySetting
    var q = this.state.NavbarState.suspectCases
    if(q !== undefined){
      if(this.state.NavbarState.suspectCases.length > 0 && this.state.NavbarState.movements.length > 0 ){
        displaySetting = "block"
      }
      else{
        displaySetting = "none"
      }
    }



    return(
      <div className="App">
        <NavBar handleData={this.handleNavbar}/>
        <Graph display={displaySetting}/>
      </div>
    );
  }
}

export default App;
