import React, { useRef } from 'react'
import * as rb from 'react-bootstrap'
import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';

function Table(props){
    if(props.type === "CC"){
        return(
            <div style={{display:props.display}}>

                <BootstrapTable data={ props.tableprops }>
                    <TableHeaderColumn dataField='caseNumber' isKey>Case Number</TableHeaderColumn>
                    <TableHeaderColumn dataField='UID'>User ID</TableHeaderColumn>
                    <TableHeaderColumn dataField='name'>Name</TableHeaderColumn>
                    <TableHeaderColumn dataField='place'>Place At</TableHeaderColumn>
                    <TableHeaderColumn dataField='datetime'>Date and Time</TableHeaderColumn>
                </BootstrapTable>
            </div>
        )
    }
    else{
        return(
            <div style={{display:props.display}}>
                <rb.Table striped bordered hover responsive>
                    <thead>
                        <tr>
                            <th>User ID</th>
                            <th>Name</th>
                            <th>Place At</th>
                            <th>Date and Time</th>
                        </tr>
                    </thead>
                    <tbody>
                        {props.tableprops}
                    </tbody>
                </rb.Table>
            </div>
        )
    }
    
}

export default Table
