class App extends React.Component{
  render(){
    return (
      <div>
        <Header/>
        <Greeting/>
      </div>
    )
  }
}

class Header extends React.Component{
  render(){
    return (
      <header>
        <p>Welcome, {this.props.username}!</p>
      </header>
    )
  }
}

class Greeting extends React.Component{
  render(){

    const date = new Date()
    const hours = date.getHours()
    let timeOdDay

    if(hours < 12){
      timeOdDay = "morning"
    }
    else if (hours >= 12 && hours < 17){
      timeOdDay = "afternoon"
    }
    else{
      timeOdDay = "night"
    }

    return (
      <h1>Good {timeOdDay} to you, sir or madam!</h1>
    )
  }
}

export default App
