  }

  return (
    <div>
      <h1 style = {{color: "#000000", backgroundColor:"#ffffff"}}>{timeOfDay + " " + firstname + " " + lastname}</h1>
      <h1>My name is {firstname + " " + lastname}</h1>
      <NavBar/>
      <MyInfo/>
      <Footer/>
    </div>
  );
}

export default App;
